import numpy as np
import re
def ascii_encode(filename):
    file = open(filename)
    charcode = ''
    for r,line in enumerate(file):
        for c,char in enumerate(line):
            if char not in (' '):
                charcode += '{:02d}{}'.format(c,char)
#    print repr(charcode)
    return charcode

def easteregg(charcode):
    regexp = re.compile(r'(\d\d)(.)', re.DOTALL)
    last_pos = 0
    outstring = ''
    for m in regexp.finditer(charcode):
        index = int(m.groups()[0])
        char = m.groups()[1]
        outstring += (index-last_pos)*' ' + char
        last_pos = index+1
        if char == '\n': 
            last_pos = 0
    return outstring

